import unittest
import requests


class APITestCase(unittest.TestCase):

    BASE_URL = 'http://localhost:5001'

    def test_login_then_details_success(self):
        payload = {
            "email": "testmelody7@gmail.com",
            "password": "Password!0"
        }
        response = requests.post(f'{self.BASE_URL}/auth/login', json=payload)
        self.assertEqual(response.status_code, 200)
        self.assertIn('access_token', response.json())
        token = response.json()['access_token']

        headers = {
        'Authorization': f'Bearer {token}'
        }

        user_details_response = requests.get(f'{self.BASE_URL}/profile/863f4d6d-8917-4d8b-a652-ccb639ef02ea', headers=headers)
        self.assertEqual(user_details_response.status_code, 200)

    def test_login_then_invalid_id(self):
        payload = {
            "email": "testmelody7@gmail.com",
            "password": "Password!0"
        }
        response = requests.post(f'{self.BASE_URL}/auth/login', json=payload)
        self.assertEqual(response.status_code, 200)
        self.assertIn('access_token', response.json())
        token = response.json()['access_token']

        headers = {
        'Authorization': f'Bearer {token}'
        }

        user_details_response = requests.get(f'{self.BASE_URL}/profile/dsada', headers=headers)
        self.assertEqual(user_details_response.status_code, 404)
    
    def test_upload_profile_picture_success(self):
        payload = {
            "email": "testmelody7@gmail.com",
            "password": "Password!0"
        }
        response = requests.post(f'{self.BASE_URL}/auth/login', json=payload)
        self.assertEqual(response.status_code, 200)
        self.assertIn('access_token', response.json())
        token = response.json()['access_token']

        headers = {
            'Authorization': f'Bearer {token}'
        }

        payload = {
            "userId": '863f4d6d-8917-4d8b-a652-ccb639ef02ea',
            "picture": 'data:image/jpeg;base64,'
        }
        response = requests.put(f'{self.BASE_URL}/profile/profile-picture', json=payload, headers=headers)

        # Check the response status and content
        self.assertEqual(response.status_code, 200)
        self.assertIn('message', response.json())
        self.assertIn('profile_picture_url', response.json())

    def test_upload_profile_picture_missing_fields(self):
        payload = {
            "email": "testmelody7@gmail.com",
            "password": "Password!0"
        }
        response = requests.post(f'{self.BASE_URL}/auth/login', json=payload)
        self.assertEqual(response.status_code, 200)
        self.assertIn('access_token', response.json())
        token = response.json()['access_token']

        headers = {
            'Authorization': f'Bearer {token}'
        }

        payload = {
            "userId": '863f4d6d-8917-4d8b-a652-ccb639ef02ea'
        }
        response = requests.put(f'{self.BASE_URL}/profile/profile-picture', json=payload, headers=headers)

        # Check the response status and content
        self.assertEqual(response.status_code, 400)
    
    def test_upload_profile_picture_unauthorized_id(self):
        payload = {
            "email": "testmelody7@gmail.com",
            "password": "Password!0"
        }
        response = requests.post(f'{self.BASE_URL}/auth/login', json=payload)
        self.assertEqual(response.status_code, 200)
        self.assertIn('access_token', response.json())
        token = response.json()['access_token']

        headers = {
            'Authorization': f'Bearer {token}'
        }

        payload = {
            "userId": 'a68966b4-ac7b-4f38-be07-d7947ca001a9',
            "picture": 'randomImage'
        }
        response = requests.put(f'{self.BASE_URL}/profile/profile-picture', json=payload, headers=headers)

        self.assertEqual(response.status_code, 403)

if __name__ == '__main__':
    unittest.main()
